import React, { useState, useEffect } from 'react';
import Card from './components/Card';
import ProgressBar from './components/ProgressBar';
import AchievementModal from './components/AchievementModal';
import useLocalStorage from './hooks/useLocalStorage';
import words from './data/words';
import './styles/App.css';

function App() {
  const [currentWord, setCurrentWord] = useState(null);
  const [correctAnswers, setCorrectAnswers] = useLocalStorage('correctAnswers', 0);
  const [learnedWords, setLearnedWords] = useLocalStorage('learnedWords', []);
  const [repeatWords, setRepeatWords] = useLocalStorage('repeatWords', []);
  const [includeLearnedWords, setIncludeLearnedWords] = useLocalStorage('includeLearnedWords', true);
  const [showAchievement, setShowAchievement] = useState(false);
  const [achievementMessage, setAchievementMessage] = useState('');

  useEffect(() => {
    selectNewWord();
  }, []);

  const selectNewWord = () => {
    const availableWords = words.filter(word =>
      !learnedWords.includes(word.english) ||
      (includeLearnedWords && Math.random() > 0.7)
    );
    const newWord = availableWords[Math.floor(Math.random() * availableWords.length)];
    setCurrentWord(newWord);
  };

  const handleCorrectAnswer = () => {
    setCorrectAnswers(prev => prev + 1);
    if (repeatWords.includes(currentWord.english)) {
      setLearnedWords(prev => [...prev, currentWord.english]);
      setRepeatWords(prev => prev.filter(word => word !== currentWord.english));
    }
    checkAchievements();
    selectNewWord();
  };

  const handleFlip = () => {
    if (!repeatWords.includes(currentWord.english)) {
      setRepeatWords(prev => [...prev, currentWord.english]);
    }
  };

  const checkAchievements = () => {
    if (correctAnswers + 1 === 10) {
      setAchievementMessage('Поздравляем! Вы правильно ответили на 10 слов!');
      setShowAchievement(true);
    }
    // Добавьте другие достижения здесь
  };

  return (
    <div className="App">
      <ProgressBar correctAnswers={correctAnswers} learnedWords={learnedWords.length} />
      {currentWord && (
        <Card
          word={currentWord}
          onCorrectAnswer={handleCorrectAnswer}
          onFlip={handleFlip}
        />
      )}
      <div className="settings">
        <label>
          <input
            type="checkbox"
            checked={includeLearnedWords}
            onChange={() => setIncludeLearnedWords(!includeLearnedWords)}
          />
          Включить изученные слова
        </label>
      </div>
      <AchievementModal
        show={showAchievement}
        message={achievementMessage}
        onClose={() => setShowAchievement(false)}
      />
    </div>
  );
}

export default App;
