import React, { useState } from "react";

function WordList({
  words,
  userWords,
  learnedWords,
  repeatWords,
  setLearnedWords,
  currentPage,
  setCurrentPage,
  wordsPerPage,
}) {
  const [searchTerm, setSearchTerm] = useState("");
  const [sortMode, setSortMode] = useState("alphabet");

  const allWords = [...words, ...userWords];

  const filteredWords = allWords.filter((word) =>
    word.english.toLowerCase().includes(searchTerm.toLowerCase()),
  );

  const sortedWords = filteredWords.sort((a, b) => {
    if (sortMode === "alphabet") {
      return a.english.localeCompare(b.english);
    } else if (sortMode === "difficulty") {
      const aErrors = repeatWords.includes(a.english) ? 1 : 0;
      const bErrors = repeatWords.includes(b.english) ? 1 : 0;
      return bErrors - aErrors;
    } else if (sortMode === "learned") {
      const aLearned = learnedWords.includes(a.english) ? 1 : 0;
      const bLearned = learnedWords.includes(b.english) ? 1 : 0;
      return bLearned - aLearned;
    }
    return 0;
  });

  // Пагинация
  const indexOfLastWord = currentPage * wordsPerPage;
  const indexOfFirstWord = indexOfLastWord - wordsPerPage;
  const currentWords = sortedWords.slice(indexOfFirstWord, indexOfLastWord);

  const totalPages = Math.ceil(sortedWords.length / wordsPerPage);

  const toggleLearned = (word) => {
    if (learnedWords.includes(word.english)) {
      setLearnedWords(learnedWords.filter((w) => w !== word.english));
    } else {
      setLearnedWords([...learnedWords, word.english]);
    }
  };

  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div>
      <input
        type="text"
        placeholder="Поиск..."
        value={searchTerm}
        onChange={(e) => setSearchTerm(e.target.value)}
      />
      <select value={sortMode} onChange={(e) => setSortMode(e.target.value)}>
        <option value="alphabet">Алфавит</option>
        <option value="difficulty">Сложность</option>
        <option value="learned">Изученные</option>
      </select>
      <ul>
        {currentWords.map((word, index) => (
          <li key={index}>
            <span>
              {word.english} - {word.russian.join(", ")}
            </span>
            <button onClick={() => toggleLearned(word)}>
              {learnedWords.includes(word.english)
                ? "Удалить из изученных"
                : "Добавить в изученные"}
            </button>
          </li>
        ))}
      </ul>
      <div className="pagination">
        {Array.from({ length: totalPages }, (_, index) => (
          <button
            key={index}
            onClick={() => handlePageChange(index + 1)}
            className={currentPage === index + 1 ? "active" : ""}
          >
            {index + 1}
          </button>
        ))}
      </div>
    </div>
  );
}

export default WordList;
