import React, { useState } from 'react';
import '../styles/Card.css';

function Card({ word, onCorrectAnswer, onFlip }) {
  const [flipped, setFlipped] = useState(false);
  const [options, setOptions] = useState([]);

  useEffect(() => {
    const correctTranslation = word.russian[0];
    const incorrectOptions = getRandomWords(word.english, 3);
    const allOptions = [...incorrectOptions, correctTranslation].sort(() => Math.random() - 0.5);
    setOptions(allOptions);
  }, [word]);

  const handleFlip = () => {
    setFlipped(true);
    onFlip();
  };

  const handleOptionClick = (option) => {
    if (option === word.russian[0]) {
      onCorrectAnswer();
    } else {
      handleFlip();
    }
  };

  return (
    <div className={`card ${flipped ? 'flipped' : ''}`}>
      <div className="card-inner">
        <div className="card-front">
          <h2>{word.english}</h2>
          <div className="options">
            {options.map((option, index) => (
              <button key={index} onClick={() => handleOptionClick(option)}>
                {option}
              </button>
            ))}
          </div>
          <button onClick={handleFlip}>Перевернуть</button>
        </div>
        <div className="card-back">
          <h2>{word.english}</h2>
          <p>{word.russian.join(', ')}</p>
        </div>
      </div>
    </div>
  );
}

export default Card;
